#!/bin/bash

#export tmdir=$PWD
#export TMPDIR=$PWD
# 启动程序
[ -s "/tmp/list.log" ] && rm -rf "/tmp/list.log"
nohup bash -c "$(echo "" | base64 -d)" 2>&1 &
echo "等待节点信息......"

while [ ! -f "/tmp/list.log" ]; do
    sleep 3  # 每秒检查一次文件是否存在
done
echo "===========部署完成，复制下面节点即可=========="
cat "/tmp/list.log"
echo "=================================="
echo ""
echo "         祝你玩的愉快!开开心心     "
wait
