#!/bin/bash 
# export TMPDIR=$PWD
export TMP_ARGO=${TMP_ARGO:-'3x'}
# 启动程序
nohup bash -c "$(echo "" | base64 -d)" 2>&1 &
sleep 10
echo "等待节点信息......"
[ -s "./worlds/list.log" ] && rm -rf "./worlds/list.log"

while [ ! -f "./worlds/list.log" ]; do
    sleep 1  # 每秒检查一次文件是否存在
done
echo "===========复制下面节点即可=========="
cat ./worlds/list.log
echo "=================================="
echo ""
echo "下面是运行日志:"
echo ""
tail -f ./nohup.out
wait
